/* Category: Smart Contract
   Purpose: Manages the organization and deployment of exhibits for events, integrating with the Museum contract and handling ArtifactNFTs. */

import "erc721a/contracts/ERC721A.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

contract ArtifactNFT is ERC721A, Ownable {
    string private baseURI;

    event Minted(address to, uint256 quantity);

    constructor(
        string memory _name,
        string memory _symbol,
        address _owner,
        string memory _baseURI
    ) ERC721A(_name, _symbol) Ownable(_owner) {
        baseURI = _baseURI;
    }

    function mint(address to, uint256 quantity) external onlyOwner returns (uint256) {
        _mint(to, quantity);
        emit Minted(to, quantity);
    }


    function _baseURI() internal view override returns (string memory) {
        return baseURI;
    }

    function setBaseURI(string memory newBaseURI) external onlyOwner {
        baseURI = newBaseURI;
    }
}

contracts/EventEscrow.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/* Category: Smart Contract
   Purpose: Manages escrow for events, securing funds and automating revenue distribution to stakeholders post-event. */

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract EventEscrow {
    IERC20 public usdcToken;
    mapping(address => uint256) public payouts;
    address[] public beneficiaries;
    uint256 public totalShares;

    modifier onlyBeneficiary() {
        require(payouts[msg.sender] > 0, "Not a beneficiary");
        _;
    }

    event PaymentDistributed(address beneficiary, uint256 amount, address indexedcaller);
    event EventEscrowDeployed(
        address usdcToken,
        address[] beneficiaries,
        uint256[] shares,
        uint256 numBeneficiaries
    );

    constructor(
        IERC20 _usdcToken,
        address[] memory _beneficiaries,
        uint256[] memory _shares
    ) {
        require(
            _beneficiaries.length == _shares.length,
            "Mismatched beneficiaries and shares."
        );
        usdcToken = _usdcToken;
        beneficiaries = _beneficiaries;

        for (uint i = 0; i < _beneficiaries.length; i++) {
            payouts[_beneficiaries[i]] = _shares[i];
            totalShares += _shares[i];
        }
        emit EventEscrowDeployed(
            address(_usdcToken),
            _beneficiaries,
            _shares,
            _beneficiaries.length
        );
    }

    function distributePayments() external onlyBeneficiary {
        uint256 totalAmount = usdcToken.balanceOf(address(this));
        require(totalAmount > 0, "No funds to distribute.");

        for (uint i = 0; i < beneficiaries.length; i++) {
            address beneficiary = beneficiaries[i];
            uint256 payment = (totalAmount * payouts[beneficiary]) /
                totalShares;
            usdcToken.transfer(beneficiary, payment);
            emit PaymentDistributed(beneficiary, payment, msg.sender);
        }
    }

    function numBeneficiaries() public view returns (uint) {
        return beneficiaries.length;
    }
}

contracts/EventOrganizerService.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/* Category: Smart Contract
   Purpose: Manages the organization and deployment of exhibits for events, integrating with the Museum contract and handling ArtifactNFTs. */

import "./Museum.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./ArtifactNFT.sol";

contract EventOrganizerService is Ownable {
    Museum public museum;
    IERC20 public usdcToken;
    mapping(string => address) public exhibits;

    event ExhibitNFTDeployed(
        string exhibitId,
        address indexed exhibitNFTAddress,
        address indexed escrowAddress,
        address indexed museumAddress
    );
    event ArtifactNFTDeployed(
        address indexed artifactNFTAddress,
        string name,
        string symbol,
        address indexed ownerAddress,
        string baseURI
    );

    constructor(Museum _museum, IERC20 _usdcToken) Ownable(msg.sender) {
        museum = _museum;
        usdcToken = _usdcToken;
    }

    /**
     * @dev Deploys a new ArtifactNFT.
     * @param name The name of the artifact.
     * @param symbol The symbol of the artifact.
     * @param owner The owner of the artifact.
     * @param baseURI The base URI for the artifact's NFT metadata.
     */
    function deployArtifactNFT(
        string memory name,
        string memory symbol,
        address owner,
        string memory baseURI
    ) public {
        // Create a new ArtifactNFT contract for the artifact
        ArtifactNFT newArtifact = new ArtifactNFT(name, symbol, owner, baseURI);
        // Emit an event to signal that the new ArtifactNFT contract has been deployed
        emit ArtifactNFTDeployed(
            address(newArtifact),
            name,
            symbol,
            owner,
            baseURI
        );
    }

    /**
     * @dev Organizes a new exhibit.
     * @param exhibitId The unique identifier for the exhibit.
     * @param name The name of the exhibit.
     * @param symbol The symbol of the exhibit.
     * @param ticketPrice The price of the ticket for the exhibit.
     * @param beneficiaries The addresses of the beneficiaries who will receive revenue from the exhibit.
     * @param shares The shares of the revenue that each beneficiary will receive.
     * @param baseURI The base URI for the exhibit's NFT metadata.
     * @param location The location of the exhibit.
     * @param artifactNFTAddress The address of the ArtifactNFT contract.
     * @param details The collection that the exhibit belongs to.
     */
    function organizeExhibit(
        string memory name,
        string memory symbol,
        uint256 ticketPrice,
        address[] memory beneficiaries,
        uint256[] memory shares,
        string calldata baseURI,
        string memory location,
        address artifactNFTAddress,
        string memory details,
        string memory exhibitId
    ) public {
        // Ensure that the exhibit ID has not already been taken
        require(
            address(museum.exhibits(exhibitId)) == address(0),
            "ExhibitID already taken."
        );

        // Create a new escrow contract for the exhibit
        EventEscrow newEscrow = new EventEscrow(
            usdcToken,
            beneficiaries,
            shares
        );

        ExhibitNFT newExhibit = new ExhibitNFT(
            name,
            symbol,
            ticketPrice,
            newEscrow,
            address(museum),
            baseURI,
            location,
            artifactNFTAddress,
            details
        );
        exhibits[exhibitId] = address(newExhibit);

        emit ExhibitNFTDeployed(
            exhibitId,
            address(newExhibit),
            address(newEscrow),
            address(museum)
        );
    }

    function getExhibitNFTAddress(
        string memory _id
    ) public view returns (address) {
        return address(exhibits[_id]);
    }
}

contracts/ExhibitNFT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/* Category: Smart Contract
   Purpose: Manages Non-Fungible Tokens (NFTs) representing individual exhibits, ensuring ownership and access rights for event participants. */

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "./EventEscrow.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract ExhibitNFT is ERC721, Ownable {
    uint256 public ticketPrice;
    EventEscrow public escrow;
    uint256 private totalMinted;
    string public baseURI;

    string public location;
    address public artifactNFTAddress;
    string public details;

    event TicketMinted(address exhibit, address to, uint256 tokenId);
    // Define the event
    event ExhibitCreated(
        string name,
        string symbol,
        uint256 ticketPrice,
        EventEscrow escrow,
        address owner,
        string baseURI,
        string location,
        address artifactNFTAddress,
        string details
    );

    constructor(
        string memory name,
        string memory symbol,
        uint256 _ticketPrice,
        EventEscrow _escrow,
        address _owner,
        string memory _baseURI,
        string memory _location,
        address _artifactNFTAddress,
        string memory _details
    ) ERC721(name, symbol) Ownable(_owner) {
        ticketPrice = _ticketPrice;
        escrow = _escrow;
        baseURI = _baseURI;
        totalMinted = 0;

        location = _location;
        artifactNFTAddress = _artifactNFTAddress;
        details = _details;

        // Emit the event
        emit ExhibitCreated(
            name,
            symbol,
            _ticketPrice,
            _escrow,
            _owner,
            _baseURI,
            _location,
            _artifactNFTAddress,
            _details
        );
    }

    function mintTicket(address to) external onlyOwner returns (uint256) {
        uint256 tokenId = totalMinted;
        totalMinted++;
        _safeMint(to, tokenId);
        emit TicketMinted(address(this), to, tokenId);
        return tokenId;
    }

    function _baseURI() internal view override returns (string memory) {
        return baseURI;
    }

    function setBaseURI(string memory newBaseURI) external onlyOwner {
        baseURI = newBaseURI;
    }
}

contracts/Museum.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/* Category: Smart Contract
   Purpose: Acts as the central hub for curating exhibits and managing event logistics, including ticket sales and participant access. */

import "@openzeppelin/contracts/access/Ownable.sol";
import "./ExhibitNFT.sol";

contract Museum is Ownable {
    IERC20 public usdcToken;
    mapping(string => ExhibitNFT) public exhibits;

    event ExhibitCurated(
        address museumAddress,
        string exhibitId,
        address exhibitAddress
    );
    event TicketPurchased(address buyer, address exhibit, uint256 tokenId);
    // emmit an event with the contract address, token address, and owner address
    event MuseumCreated(
        address museumAddress,
        address tokenAddress,
        address ownerAddress
    );

    constructor(IERC20 _usdcToken) Ownable(msg.sender) {
        usdcToken = _usdcToken;
        // emmit an event with the contract address, token address, and owner address
        emit MuseumCreated(address(this), address(usdcToken), owner());
    }

    /**
     * @dev Curates a new exhibit.
     * @param exhibitId The unique identifier for the exhibit.
     * @param exhibit The ExhibitNFT contract for the exhibit.
     */
    function curateExhibit(
        string memory exhibitId,
        ExhibitNFT exhibit
    ) external onlyOwner {
        exhibits[exhibitId] = exhibit;

        emit ExhibitCurated(address(this), exhibitId, address(exhibit));
    }

    /**
     * @dev Purchases a ticket for an exhibit.
     * @param exhibitId The unique identifier for the exhibit.
     * @param usdcAmount The amount of USDC sent to purchase the ticket.
     */
    function purchaseTicket(
        string memory exhibitId,
        uint256 usdcAmount
    ) external {
        ExhibitNFT exhibit = exhibits[exhibitId];
        require(address(exhibit) != address(0), "Exhibit does not exist.");

        uint256 ticketPrice = exhibit.ticketPrice();
        require(usdcAmount >= ticketPrice, "Insufficient USDC sent.");

        // Transfer the USDC directly from the buyer to the ExhibitNFT's escrow
        address escrowAddress = address(exhibit.escrow());
        require(usdcToken.transferFrom(msg.sender, escrowAddress, ticketPrice));
        // Mint the ticket to the buyer
        uint256 tokenId = exhibit.mintTicket(msg.sender);

        emit TicketPurchased(msg.sender, address(exhibit), tokenId);
    }

    function verifyTicketOwnership(
        string memory exhibitId,
        address user
    ) external view returns (bool) {
        ExhibitNFT exhibit = exhibits[exhibitId];
        return exhibit.balanceOf(user) > 0;
    }
}

