// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/* Category: Smart Contract
   Purpose: Facilitates the conversion between different currencies or tokens, supporting financial operations within the platform. */

import '@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol';
import '@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol';
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";


contract ConversionContract {
    ISwapRouter public swapRouter;
    address public controller;
    address public usdt;
    address public usdc;
    address public storageContract;

    function setStorageContract(address _storageContract) public{
        storageContract = _storageContract;
    }

    event Conversion(uint256 amountIn, uint256 amountOut, address indexed storageContract);

    modifier onlyController() {
        require(msg.sender == controller, "Only the controller can perform this action.");
        _;
    }

    constructor(
    address _uniswapRouter, 
    address _controller, 
    address _usdt, 
    address _usdc,
    address _storageContract
    
){}

    function initialize(
        address _swapRouter,
        address _controller,
        address _usdt,
        address _usdc,
        address _storageContract
    ) external {
        require(controller == address(0), "Controller has already been set.");
        
        swapRouter = ISwapRouter(_swapRouter);
        controller = _controller;
        usdt = _usdt;
        usdc = _usdc;
        storageContract = _storageContract;
    }

    function initialApprove() external onlyController {
        TransferHelper.safeApprove(usdt, address(swapRouter), type(uint256).max);
    }

    function convertUSDTtoUSDC(uint256 amountIn, uint256 amountOutMinimum) external onlyController returns (uint256) {
       address[] memory path = new address[](2);
       path[0] = usdt;
       path[1] = usdc;
       
       bytes memory encodedPath = abi.encode(path); // Convert address[] to bytes
       
       uint256 amountOut = swapRouter.exactInput(
           ISwapRouter.ExactInputParams({
               path: encodedPath,
               recipient: storageContract,
               deadline: block.timestamp + 15,
               amountIn: amountIn,
               amountOutMinimum: amountOutMinimum
           })
       );
       
       emit Conversion(amountIn, amountOut, storageContract);
       return amountOut;
   }}

contracts/Donations.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title SummitShare Donation Contract
 * @dev Manages donations for projects or communities within the SummitShare platform.
 */
contract Donations is Ownable(msg.sender) {
    using SafeERC20 for IERC20;

    address private projectWallet;
    address private communityWallet;

    // Event to log the donation details
    event DonationReceived(
        address indexed donor,
        uint256 amount,
        address indexed tokenAddress,
        string recipientType,
        string donorName,
        bool isAnonymous
    );

    constructor(address _projectWallet, address _communityWallet) {
        projectWallet = _projectWallet;
        communityWallet = _communityWallet;
    }

    /**
     * @dev Allows donations to be made to the project or community.
     * @param amount The amount of tokens to donate.
     * @param tokenAddress The address of the ERC20 token used for donation.
     * @param toProject True if the donation goes to the project, false for community.
     * @param donorName Name of the donor, optional.
     * @param isAnonymous Whether the donation should be anonymous.
     */
    function donate(uint256 amount, address tokenAddress, bool toProject, string calldata donorName, bool isAnonymous) external {
        address recipient = toProject ? projectWallet : communityWallet;
        IERC20(tokenAddress).safeTransferFrom(msg.sender, recipient, amount);

        emit DonationReceived(msg.sender, amount, tokenAddress, toProject ? "Project" : "Community", donorName, isAnonymous);
    }

    /**
     * @dev Allows the owner to update the wallet addresses.
     * @param newProjectWallet The new project wallet address.
     * @param newCommunityWallet The new community wallet address.
     */
    function updateWalletAddresses(address newProjectWallet, address newCommunityWallet) public onlyOwner {
        projectWallet = newProjectWallet;
        communityWallet = newCommunityWallet;
    }

    /**
     * @dev Retrieve the current project and community wallet addresses.
     */
    function getWalletAddresses() public view returns (address, address) {
        return (projectWallet, communityWallet);
    }
}

contracts/EventEscrow.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/* Category: Smart Contract
   Purpose: Manages escrow for events, securing funds and automating revenue distribution to stakeholders post-event. */

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract EventEscrow {
    IERC20 public usdcToken;
    mapping(address => uint256) public payouts;
    address[] public beneficiaries;
    uint256 public totalShares;

    modifier onlyBeneficiary() {
        require(payouts[msg.sender] > 0, "Not a beneficiary");
        _;
    }

    event PaymentDistributed(address beneficiary, uint256 amount, address indexedcaller);
    event EventEscrowDeployed(
        address usdcToken,
        address[] beneficiaries,
        uint256[] shares,
        uint256 numBeneficiaries
    );

    constructor(
        IERC20 _usdcToken,
        address[] memory _beneficiaries,
        uint256[] memory _shares
    ) {
        require(
            _beneficiaries.length == _shares.length,
            "Mismatched beneficiaries and shares."
        );
        usdcToken = _usdcToken;
        beneficiaries = _beneficiaries;

        for (uint i = 0; i < _beneficiaries.length; i++) {
            payouts[_beneficiaries[i]] = _shares[i];
            totalShares += _shares[i];
        }
        emit EventEscrowDeployed(
            address(_usdcToken),
            _beneficiaries,
            _shares,
            _beneficiaries.length
        );
    }

    function distributePayments() external onlyBeneficiary {
        uint256 totalAmount = usdcToken.balanceOf(address(this));
        require(totalAmount > 0, "No funds to distribute.");

        for (uint i = 0; i < beneficiaries.length; i++) {
            address beneficiary = beneficiaries[i];
            uint256 payment = (totalAmount * payouts[beneficiary]) /
                totalShares;
            usdcToken.transfer(beneficiary, payment);
            emit PaymentDistributed(beneficiary, payment, msg.sender);
        }
    }

    function numBeneficiaries() public view returns (uint) {
        return beneficiaries.length;
    }
}

