    address usdc;
    address rs2;
    address rs3;
    uint128 ps;

    constructor(
        ISwapRouter _swapRouter,
        address _usdt,
        address _usdc,
        address _rs2,
        address _rs3,
        uint128 _ps
    ) {
        swapRouter = ISwapRouter(_swapRouter);
        usdt = _usdt;
        usdc = _usdc;
        rs2 = _rs2;
        rs3 = _rs3;
        ps = _ps;
    }

        // Event to log the funding of the contract
